mapper.select:
SELECT

from cart
where user_id = #{userId}
and book_id = #{bookId}

mapper.delete:
delete from cart
where user_id = #{userId}

  and book_id in

    #{item}

mapper.update:
UPDATE  cart
set checked = #{checked},
update_time = now()
where user_id = #{userId}

  and book_id = #{bookId}
